import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate, useLocation } from 'react-router-dom';
import Login from './components/Login';
import DashboardCRUD from './components/DashboardCRUD';
import UsersManager from './components/UsersManager';
import { authService } from './utils/api';
import './css/styles.css';

const ProtectedRoute = ({ children }) => {
  const token = localStorage.getItem('token');
  return token ? children : <Navigate to="/login" />;
};

const Dashboard = () => {
  const navigate = useNavigate();
  const [usuario, setUsuario] = useState(null);
  const location = useLocation();

  useEffect(() => {
    const user = authService.getUser();
    if (!user) {
      navigate('/login');
    } else {
      setUsuario(user);
    }
  }, [navigate]);

  const handleLogout = () => {
    authService.logout();
    navigate('/login');
  };

  if (!usuario) return <div>Cargando...</div>;

  // Si es administrador y quiere ir a usuarios, renderiza UsersManager
  if (usuario.rol === 'administrador' && location.pathname === '/usuarios') {
    return <UsersManager usuario={usuario} onLogout={handleLogout} />;
  }

  // Sino, renderiza DashboardCRUD
  return <DashboardCRUD />;
};

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route
          path="/dashboard"
          element={
            <ProtectedRoute>
              <Dashboard />
            </ProtectedRoute>
          }
        />
        <Route
          path="/usuarios"
          element={
            <ProtectedRoute>
              <Dashboard />
            </ProtectedRoute>
          }
        />
        <Route path="/" element={<Navigate to="/dashboard" />} />
      </Routes>
    </Router>
  );
}

export default App;
