import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { authService, clientesService, solicitudesService, ordenesService, inventarioService, usuariosService } from '../utils/api';
import CRUDModal from './CRUDModal';
import '../css/dashboard.css';
import '../css/modal.css';

const DashboardCRUD = () => {
  const navigate = useNavigate();
  const [usuario, setUsuario] = useState(null);
  const [modulo, setModulo] = useState('clientes');
  const [clientes, setClientes] = useState([]);
  const [solicitudes, setSolicitudes] = useState([]);
  const [ordenes, setOrdenes] = useState([]);
  const [inventario, setInventario] = useState([]);
  const [usuarios, setUsuarios] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  
  // Modal states
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [editingData, setEditingData] = useState(null);
  const [isDeleting, setIsDeleting] = useState(false);
  const [deleteId, setDeleteId] = useState(null);
  
  // BÃºsqueda
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const user = authService.getUser();
    if (!user) {
      navigate('/login');
      return;
    }
    setUsuario(user);
    
    // Establecer mÃ³dulo inicial segÃºn el rol
    if (user.rol === 'tecnico' || user.rol === 'coordinador') {
      setModulo('solicitudes');
    } else {
      setModulo('clientes');
    }
    
    cargarDatos();
  }, [navigate]);

  const cargarDatos = async () => {
    setLoading(true);
    setError('');
    try {
      const [clientesRes, solicitudesRes, ordenesRes, inventarioRes, usuariosRes] = await Promise.all([
        clientesService.obtenerTodos(),
        solicitudesService.obtenerTodos(),
        ordenesService.obtenerTodos(),
        inventarioService.obtenerTodos(),
        usuariosService.obtenerTodos()
      ]);

      setClientes(clientesRes.data);
      setSolicitudes(solicitudesRes.data);
      setOrdenes(ordenesRes.data);
      setInventario(inventarioRes.data);
      setUsuarios(usuariosRes.data);
    } catch (error) {
      console.error('Error cargando datos:', error);
      setError('Error al cargar los datos. Por favor intenta de nuevo.');
    } finally {
      setLoading(false);
    }
  };

  // CRUD Clientes
  const clientesFields = [
    { name: 'nombre', label: 'Nombre', type: 'text', required: true, placeholder: 'Ej: Empresa XYZ' },
    { name: 'numero_documento', label: 'Documento', type: 'text', required: true, placeholder: '123456789' },
    { name: 'tipo_documento', label: 'Tipo Documento', type: 'select', required: true, options: [
      { value: 'NIT', label: 'NIT' },
      { value: 'CC', label: 'CÃ©dula' },
      { value: 'CE', label: 'CÃ©dula ExtranjerÃ­a' },
      { value: 'RUC', label: 'RUC' },
      { value: 'PASAPORTE', label: 'Pasaporte' }
    ]},
    { name: 'email', label: 'Email', type: 'email', required: true },
    { name: 'ciudad', label: 'Ciudad', type: 'text', required: true },
    { name: 'direccion', label: 'DirecciÃ³n', type: 'text', required: true },
    { name: 'telefono', label: 'TelÃ©fono', type: 'text', required: true },
    { name: 'contacto_principal', label: 'Contacto Principal', type: 'text', required: false }
  ];

  const solicitudesFields = [
    { name: 'id_cliente', label: 'Cliente', type: 'select', required: true, options: clientes.map(c => ({ value: c.id_cliente, label: c.nombre })) },
    { name: 'nombre_solicitud', label: 'Nombre', type: 'text', required: true },
    { name: 'descripcion', label: 'DescripciÃ³n', type: 'textarea', required: false },
    { name: 'prioridad', label: 'Prioridad', type: 'select', required: true, options: [
      { value: 'baja', label: 'Baja' },
      { value: 'normal', label: 'Normal' },
      { value: 'alta', label: 'Alta' },
      { value: 'urgente', label: 'Urgente' }
    ]},
    { name: 'estado', label: 'Estado', type: 'select', required: true, options: [
      { value: 'en_curso', label: 'En Curso' },
      { value: 'finalizado', label: 'Finalizado' },
      { value: 'cancelado', label: 'Cancelado' },
      { value: 'en_espera', label: 'En Espera' }
    ]}
  ];

  const ordenesFields = [
    { name: 'id_solicitud', label: 'Solicitud', type: 'select', required: true, options: solicitudes.map(s => ({ value: s.id_solicitud, label: s.nombre_solicitud })) },
    { name: 'id_cliente', label: 'Cliente', type: 'select', required: true, options: clientes.map(c => ({ value: c.id_cliente, label: c.nombre })) },
    { name: 'id_tecnico', label: 'TÃ©cnico', type: 'select', required: false, options: [{ value: '', label: 'Sin asignar' }, ...usuarios.filter(u => u.rol === 'tecnico').map(u => ({ value: u.id_usuario, label: u.nombre }))] },
    { name: 'fecha_inicio', label: 'Fecha Inicio', type: 'date', required: true },
    { name: 'fecha_cierre', label: 'Fecha Cierre', type: 'date', required: false },
    { name: 'estado', label: 'Estado', type: 'select', required: true, options: [
      { value: 'pendiente', label: 'Pendiente' },
      { value: 'en_proceso', label: 'En Proceso' },
      { value: 'finalizado', label: 'Finalizado' },
      { value: 'cancelado', label: 'Cancelado' },
      { value: 'suspendido', label: 'Suspendido' }
    ]},
    { name: 'observaciones', label: 'Observaciones', type: 'textarea', required: false }
  ];

  const inventarioFields = [
    { name: 'codigo_interno', label: 'CÃ³digo', type: 'text', required: true },
    { name: 'nombre', label: 'Nombre', type: 'text', required: true },
    { name: 'descripcion', label: 'DescripciÃ³n', type: 'textarea', required: false },
    { name: 'categoria', label: 'CategorÃ­a', type: 'text', required: true },
    { name: 'cantidad', label: 'Cantidad', type: 'number', required: true },
    { name: 'cantidad_minima', label: 'Cantidad MÃ­nima', type: 'number', required: true },
    { name: 'proveedor', label: 'Proveedor', type: 'text', required: false },
    { name: 'precio_unitario', label: 'Precio Unitario', type: 'number', required: false }
  ];

  const handleNew = () => {
    // TÃ©cnico no puede crear solicitudes
    if (modulo === 'solicitudes' && usuario.rol === 'tecnico') {
      setError('Los tÃ©cnicos solo pueden ver las solicitudes, no crearlas');
      return;
    }
    setEditingId(null);
    setEditingData(null);
    setIsModalOpen(true);
  };

  const handleEdit = (record) => {
    setEditingId(record.id_cliente || record.id_solicitud || record.id_item);
    setEditingData(record);
    setIsModalOpen(true);
  };

  const handleDelete = async () => {
    try {
      setIsDeleting(true);
      if (modulo === 'clientes') {
        await clientesService.eliminar(deleteId);
        setClientes(clientes.filter(c => c.id_cliente !== deleteId));
      } else if (modulo === 'solicitudes') {
        await solicitudesService.eliminar(deleteId);
        setSolicitudes(solicitudes.filter(s => s.id_solicitud !== deleteId));
      } else if (modulo === 'ordenes') {
        await ordenesService.eliminar(deleteId);
        setOrdenes(ordenes.filter(o => o.id_orden !== deleteId));
      } else if (modulo === 'inventario') {
        await inventarioService.eliminar(deleteId);
        setInventario(inventario.filter(i => i.id_item !== deleteId));
      }
      setDeleteId(null);
    } catch (error) {
      console.error('Error al eliminar:', error);
      const errorMessage = error.response?.data?.error || error.message || 'Error al eliminar.';
      setError(`âŒ ${errorMessage}`);
    } finally {
      setIsDeleting(false);
    }
  };

  const handleSave = async (formData) => {
    try {
      setLoading(true);
      
      if (modulo === 'clientes') {
        if (editingId) {
          await clientesService.actualizar(editingId, formData);
          setClientes(clientes.map(c => c.id_cliente === editingId ? { ...c, ...formData } : c));
        } else {
          const res = await clientesService.crear(formData);
          setClientes([...clientes, res.data]);
        }
      } else if (modulo === 'solicitudes') {
        if (editingId) {
          await solicitudesService.actualizar(editingId, formData);
          setSolicitudes(solicitudes.map(s => s.id_solicitud === editingId ? { ...s, ...formData } : s));
        } else {
          const res = await solicitudesService.crear(formData);
          setSolicitudes([...solicitudes, res.data]);
        }
      } else if (modulo === 'ordenes') {
        if (editingId) {
          await ordenesService.actualizar(editingId, formData);
          setOrdenes(ordenes.map(o => o.id_orden === editingId ? { ...o, ...formData } : o));
        } else {
          // Enriquecer datos de orden con info de solicitud, cliente y tÃ©cnico
          const solicitud = solicitudes.find(s => s.id_solicitud === parseInt(formData.id_solicitud));
          const cliente = clientes.find(c => c.id_cliente === parseInt(formData.id_cliente));
          const tecnico = usuarios.find(u => u.id_usuario === parseInt(formData.id_tecnico));
          
          const ordenData = {
            ...formData,
            nombre_solicitud: solicitud?.nombre_solicitud || '',
            nombre_cliente: cliente?.nombre || '',
            nombre_tecnico: tecnico?.nombre || ''
          };
          
          const res = await ordenesService.crear(ordenData);
          setOrdenes([...ordenes, res.data]);
        }
      } else if (modulo === 'inventario') {
        if (editingId) {
          await inventarioService.actualizar(editingId, formData);
          setInventario(inventario.map(i => i.id_item === editingId ? { ...i, ...formData } : i));
        } else {
          const res = await inventarioService.crear(formData);
          setInventario([...inventario, res.data]);
        }
      }
      
      setIsModalOpen(false);
      setEditingId(null);
      setEditingData(null);
    } catch (error) {
      console.error('Error al guardar:', error);
      const errorMessage = error.response?.data?.error || error.message || 'Error al guardar. Intenta de nuevo.';
      setError(`âŒ ${errorMessage}`);
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    authService.logout();
    navigate('/login');
  };

  const getFilteredData = () => {
    const data = {
      clientes: clientes,
      solicitudes: solicitudes,
      ordenes: ordenes,
      inventario: inventario
    }[modulo];

    if (!searchTerm) return data;
    
    return data.filter(item => {
      const values = Object.values(item).join('').toLowerCase();
      return values.includes(searchTerm.toLowerCase());
    });
  };

  const filteredData = getFilteredData();

  if (!usuario) return <div>Cargando...</div>;

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <aside className="sidebar">
        <div className="sidebar-header">
          <h2>ERP</h2>
          <p className="user-info">
            <strong>{usuario.nombre}</strong>
            <br />
            Rol: {usuario.rol}
          </p>
        </div>

        <nav className="modules-menu">
          <h3>MÃ³dulos</h3>
          <ul>
            {/* Administrador: ve todos */}
            {usuario.rol === 'administrador' && (
              <>
                <li
                  onClick={() => { setModulo('clientes'); setSearchTerm(''); }}
                  className={modulo === 'clientes' ? 'active' : ''}
                >
                  ðŸ‘¥ Clientes
                </li>
                <li
                  onClick={() => { setModulo('solicitudes'); setSearchTerm(''); }}
                  className={modulo === 'solicitudes' ? 'active' : ''}
                >
                  ðŸ“‹ Solicitudes
                </li>
                <li
                  onClick={() => { setModulo('ordenes'); setSearchTerm(''); }}
                  className={modulo === 'ordenes' ? 'active' : ''}
                >
                  ðŸ“¦ Ã“rdenes
                </li>
                <li
                  onClick={() => { setModulo('inventario'); setSearchTerm(''); }}
                  className={modulo === 'inventario' ? 'active' : ''}
                >
                  ðŸ“Š Inventario
                </li>
                <li
                  onClick={() => { navigate('/usuarios'); }}
                  className={modulo === 'usuarios' ? 'active' : ''}
                >
                  ðŸ‘¥ GestiÃ³n de Usuarios
                </li>
              </>
            )}

            {/* TÃ©cnico: solo Solicitudes y Ã“rdenes */}
            {usuario.rol === 'tecnico' && (
              <>
                <li
                  onClick={() => { setModulo('solicitudes'); setSearchTerm(''); }}
                  className={modulo === 'solicitudes' ? 'active' : ''}
                >
                  ðŸ“‹ Solicitudes
                </li>
                <li
                  onClick={() => { setModulo('ordenes'); setSearchTerm(''); }}
                  className={modulo === 'ordenes' ? 'active' : ''}
                >
                  ðŸ“¦ Ã“rdenes Asignadas
                </li>
              </>
            )}

            {/* Coordinador: Solicitudes y Ã“rdenes */}
            {usuario.rol === 'coordinador' && (
              <>
                <li
                  onClick={() => { setModulo('solicitudes'); setSearchTerm(''); }}
                  className={modulo === 'solicitudes' ? 'active' : ''}
                >
                  ðŸ“‹ Solicitudes
                </li>
                <li
                  onClick={() => { setModulo('ordenes'); setSearchTerm(''); }}
                  className={modulo === 'ordenes' ? 'active' : ''}
                >
                  ðŸ“¦ Ã“rdenes
                </li>
              </>
            )}
          </ul>
        </nav>

        <button className="btn-logout" onClick={handleLogout}>
          ðŸšª Cerrar SesiÃ³n
        </button>
      </aside>

      {/* Main Content */}
      <main className="content">
        <header className="content-header">
          <h1>
            {modulo === 'usuarios' ? 'ðŸ‘¥ GestiÃ³n de Usuarios' : modulo.charAt(0).toUpperCase() + modulo.slice(1)}
          </h1>
        </header>

        {error && <div className="error-message">{error}</div>}

        {/* Solo mostrar contenido si el mÃ³dulo no es usuarios o si es admin */}
        {modulo === 'usuarios' ? (
          // Este mÃ³dulo serÃ¡ renderizado por el componente padre
          <div className="no-data">
            <p>âš ï¸ Por favor usa el componente UsersManager</p>
          </div>
        ) : (
          <>
            <div className="crud-toolbar">
              <input
                type="text"
                className="search-input"
                placeholder="ðŸ” Buscar..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
              {/* Mostrar botÃ³n agregar solo si tiene permisos */}
              {((modulo !== 'solicitudes' && modulo !== 'ordenes') || (usuario.rol !== 'tecnico')) && (
                <button className="btn-add" onClick={handleNew}>
                  âž• Agregar Nuevo
                </button>
              )}
            </div>

        {loading ? (
          <div className="loading">Cargando datos...</div>
        ) : filteredData.length === 0 ? (
          <div className="no-data">
            <p>No hay datos disponibles</p>
            <button className="btn-add" onClick={handleNew}>Crear el primero</button>
          </div>
        ) : (
          <div className="table-container">
            <table>
              <thead>
                <tr>
                  {modulo === 'clientes' && (
                    <>
                      <th>ID</th>
                      <th>Nombre</th>
                      <th>Email</th>
                      <th>TelÃ©fono</th>
                      <th>Ciudad</th>
                      <th>Acciones</th>
                    </>
                  )}
                  {modulo === 'solicitudes' && (
                    <>
                      <th>ID</th>
                      <th>Solicitud</th>
                      <th>Cliente</th>
                      <th>Estado</th>
                      <th>Prioridad</th>
                      <th>Fecha</th>
                      <th>Acciones</th>
                    </>
                  )}
                  {modulo === 'ordenes' && (
                    <>
                      <th>ID</th>
                      <th>Solicitud</th>
                      <th>Cliente</th>
                      <th>TÃ©cnico</th>
                      <th>Estado</th>
                      <th>Fecha Inicio</th>
                      <th>Acciones</th>
                    </>
                  )}
                  {modulo === 'inventario' && (
                    <>
                      <th>ID</th>
                      <th>Nombre</th>
                      <th>CÃ³digo</th>
                      <th>Cantidad</th>
                      <th>MÃ­nima</th>
                      <th>CategorÃ­a</th>
                      <th>Proveedor</th>
                      <th>Acciones</th>
                    </>
                  )}
                </tr>
              </thead>
              <tbody>
                {filteredData.map((item) => (
                  <tr key={modulo === 'clientes' ? item.id_cliente : modulo === 'solicitudes' ? item.id_solicitud : modulo === 'ordenes' ? item.id_orden : item.id_item}>
                    {modulo === 'clientes' && (
                      <>
                        <td>{item.id_cliente}</td>
                        <td>{item.nombre}</td>
                        <td>{item.email}</td>
                        <td>{item.telefono}</td>
                        <td>{item.ciudad}</td>
                        <td>
                          <div className="table-actions">
                            <button className="btn-action btn-edit" onClick={() => handleEdit(item)}>Editar</button>
                            <button className="btn-action btn-delete" onClick={() => setDeleteId(item.id_cliente)}>Eliminar</button>
                          </div>
                        </td>
                      </>
                    )}
                    {modulo === 'solicitudes' && (
                      <>
                        <td>{item.id_solicitud}</td>
                        <td>{item.nombre_solicitud}</td>
                        <td>{item.nombre_cliente || `Cliente ${item.id_cliente}`}</td>
                        <td><span className={`badge badge-${item.estado}`}>{item.estado}</span></td>
                        <td><span className={`badge badge-${item.prioridad}`}>{item.prioridad}</span></td>
                        <td>{new Date(item.fecha_creacion).toLocaleDateString()}</td>
                        <td>
                          <div className="table-actions">
                            {/* TÃ©cnico solo puede ver, no editar */}
                            {usuario.rol !== 'tecnico' && (
                              <>
                                <button className="btn-action btn-edit" onClick={() => handleEdit(item)}>Editar</button>
                                <button className="btn-action btn-delete" onClick={() => setDeleteId(item.id_solicitud)}>Eliminar</button>
                              </>
                            )}
                            {usuario.rol === 'tecnico' && (
                              <span className="badge-info">ðŸ“– Lectura</span>
                            )}
                          </div>
                        </td>
                      </>
                    )}
                    {modulo === 'ordenes' && (
                      <>
                        <td>{item.id_orden}</td>
                        <td>{item.nombre_solicitud}</td>
                        <td>{item.nombre_cliente}</td>
                        <td>{item.nombre_tecnico || 'Sin asignar'}</td>
                        <td><span className={`badge badge-${item.estado}`}>{item.estado}</span></td>
                        <td>{new Date(item.fecha_inicio).toLocaleDateString()}</td>
                        <td>
                          <div className="table-actions">
                            {/* TÃ©cnico solo puede ver, no editar */}
                            {usuario.rol !== 'tecnico' && (
                              <>
                                <button className="btn-action btn-edit" onClick={() => handleEdit(item)}>Editar</button>
                                <button className="btn-action btn-delete" onClick={() => setDeleteId(item.id_orden)}>Eliminar</button>
                              </>
                            )}
                            {usuario.rol === 'tecnico' && (
                              <span className="badge-info">ðŸ“– Lectura</span>
                            )}
                          </div>
                        </td>
                      </>
                    )}
                    {modulo === 'inventario' && (
                      <>
                        <td>{item.id_item}</td>
                        <td>{item.nombre}</td>
                        <td>{item.codigo_interno}</td>
                        <td>{item.cantidad_disponible}</td>
                        <td>{item.cantidad_minima}</td>
                        <td>{item.categoria}</td>
                        <td>{item.proveedor || 'N/A'}</td>
                        <td>
                          <div className="table-actions">
                            <button className="btn-action btn-edit" onClick={() => handleEdit(item)}>Editar</button>
                            <button className="btn-action btn-delete" onClick={() => setDeleteId(item.id_item)}>Eliminar</button>
                          </div>
                        </td>
                      </>
                    )}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {/* Modal CRUD */}
        <CRUDModal
          isOpen={isModalOpen}
          title={editingId ? `Editar ${modulo}` : `Nuevo ${modulo}`}
          fields={
            modulo === 'clientes' ? clientesFields :
            modulo === 'solicitudes' ? solicitudesFields :
            modulo === 'ordenes' ? ordenesFields :
            inventarioFields
          }
          data={editingData}
          onSave={handleSave}
          onClose={() => {
            setIsModalOpen(false);
            setEditingId(null);
            setEditingData(null);
          }}
          isLoading={loading}
        />

        {/* ConfirmaciÃ³n de eliminaciÃ³n */}
        {deleteId && (
          <div className="modal-overlay">
            <div className="confirm-dialog">
              <h3>âš ï¸ Confirmar eliminaciÃ³n</h3>
              <p>Â¿EstÃ¡s seguro de que deseas eliminar este registro? Esta acciÃ³n no se puede deshacer.</p>
              <div className="confirm-actions">
                <button className="confirm-cancel" onClick={() => setDeleteId(null)} disabled={isDeleting}>
                  Cancelar
                </button>
                <button className="confirm-delete" onClick={handleDelete} disabled={isDeleting}>
                  {isDeleting ? 'Eliminando...' : 'Eliminar'}
                </button>
              </div>
            </div>
          </div>
        )}
          </>
        )}
      </main>
    </div>
  );
};

export default DashboardCRUD;
